function replaceDashesWithCommas(str) {
  return str
    // turn hyphen/en dash/em dash/horizontal bar into commas
    .replace(/[\u2012\u2013\u2014\u2015]/g, ',')
    // collapse multiple commas
    .replace(/,+/g, ',')
    // normalize spaces around commas (preserve newlines)
    .replace(/[ \t]*,[ \t]*/g, ', ')
    // remove leading/trailing spaces/tabs only (keep any \n)
    .replace(/^[ \t]+|[ \t]+$/g, '')
    // drop a leading or trailing comma (with optional spaces/tabs)
    .replace(/^[ \t]*,|,[ \t]*$/g, '');
}

$input.first().json.message.content =
  replaceDashesWithCommas($input.first().json.message.content.content);

return {content: $input.first().json.message.content}

