const item = $input.first();
const text = (item.json.message.text || '').trim();
const lower = text.toLowerCase();

let mode;
if (lower === 'start' || lower.startsWith('start ')) {
  mode = 'start';
} else if (lower === 'yes' || lower === 'post') {
  mode = 'approve_post';
} else if (text.includes('http://') || text.includes('https://')) {
  mode = 'url';
} else {
  mode = 'keyword';
}

return [{
  json: {
    mode,
    text,
    lower,
    chatId: item.json.message.chat.id
  }
}];

