function cleanSnippet(raw = '') {
  let t = raw.toString();

  // remove <script>...</script>
  t = t.replace(/<script\b[^>]*>[\s\S]*?<\/script>/gi, '');

  // remove <style>...</style> and all CSS blocks
  t = t.replace(/<style\b[^>]*>[\s\S]*?<\/style>/gi, '');
  t = t.replace(/@font-face[\s\S]*?}/gi, '');
  t = t.replace(/#[^{]+{[\s\S]*?}/gi, '');
  t = t.replace(/\.[^{]+{[\s\S]*?}/gi, '');

  // remove HTML tags
  t = t.replace(/<[^>]+>/g, '');

  // remove escaped \n or actual newlines
  t = t.replace(/\\n/g, ' ');
  t = t.replace(/\n/g, ' ');

  // collapse multiple spaces
  t = t.replace(/\s+/g, ' ');

  return t.trim();
}


const items = $input.all();
const parse = $('Parse Command').first().json;
const keyword = parse.mode === 'keyword' ? parse.lower : '';

let articles = [];

for (const item of items) {
  const j = item.json;
  const title = j.title || '';
  const link = j.link || j.id || '';

  // cleaned description
  const rawDesc = j["content:encodedSnippet"] || j.contentSnippet || '';
  const description = cleanSnippet(rawDesc);

  const pubDate = j.isoDate || j.pubDate || '';

  if (!title || !link) continue;

  if (keyword) {
    const text = (title + ' ' + description).toLowerCase();
    if (!text.includes(keyword)) continue;
  }

  articles.push({
    title,
    link,
    description,
    pubDate
  });
}

articles.sort((a, b) => {
  const da = new Date(a.pubDate || 0).getTime();
  const db = new Date(b.pubDate || 0).getTime();
  return db - da;
});

articles = articles.slice(0, 5);

const staticData = $getWorkflowStaticData('global');
staticData.lastArticles = articles;

return [{ json: { articles } }];

function replaceDashesWithCommas(str) {
  return str
    .replace(/[\u2012\u2013\u2014\u2015]/g, ',')
    .replace(/,+/g, ',')
    .replace(/[ \t]*,[ \t]*/g, ', ')
    .replace(/^[ \t]+|[ \t]+$/g, '')
    .replace(/^[ \t]*,|,[ \t]*$/g, '');
}

const input = $input.first();
if (typeof input.json.message === 'object' && input.json.message.content && input.json.message.content.content) {
  input.json.message.content = replaceDashesWithCommas(input.json.message.content.content);
}

return [{ json: { content: input.json.message.content } }];

